import SupermarketCard from "./SupermarketCard.js"
import React, {useState} from "react"

function SupermarketList({supermarkets, showDivergence}){

    const [searchResults, setSearchResults] = useState(supermarkets)

    const supermarketsToDisplay = supermarkets.map(s => {
            return (
                <SupermarketCard supermarket={s} />
            )
        })

    console.log(supermarkets)
    console.log(searchResults)

    function searchByZip (zip) {
        fetch(`http://localhost:9295/areas/zipcode/${zip}`)
        .then((r) => r.json())
        .then((data) => {setSearchResults(data)});
    }

    function filterCards(e){
        e.preventDefault()
        searchByZip(e.target.value)
    }

    console.log(searchResults)
    const filteredSupermarkets = [...supermarketsToDisplay].filter(s => {
        if (searchResults != null) {
            searchResults.includes(s)
        }
        else return true
    })



    return(
        <div>
            <form onChange={e=>filterCards(e)}>
                <input type="text" name="zipcode" placeholder="your zipcode..."/>
            </form>
            {filteredSupermarkets}
        </div>
    )
}

export default SupermarketList
