import SupermarketCard from "./SupermarketCard.js"
import React, {useState} from "react"

function SupermarketList({supermarkets, showDivergence, searchByZip}){

    const [searchResults, setSearchResults] = useState([])

    function renderSupermarkets(supermarkets){
        return supermarkets.map(s => {
            return (
                <SupermarketCard supermarket={s} />
            )
        })
    }

    function searchByZip (zip) {
        fetch(`http://localhost:9295/areas/zipcode/${zip}`)
        .then((r) => r.json())
        .then((data) => {setSearchResults(data)});
    }


    function filterCards(e){
        e.preventDefault()
        console.log(searchByZip(e.target.value))
    }


    return(
        <div>
            <form onChange={e=>filterCards(e)}>
                <input type="text" name="zipcode" placeholder="your zipcode..."/>
            </form>
            {renderSupermarkets(supermarkets)}
        </div>
    )
}

export default SupermarketList
