import SupermarketCard from "./SupermarketCard.js"
import React, {useEffect, useState} from "react"

function SupermarketList({change, setChange}){

    const [searchResults, setSearchResults] = useState([])    
    const [enteredZip, setEnteredZip] = useState(0)
    const [supermarkets, setSupermarkets] = useState([])

    useEffect(() => {
        fetch("http://localhost:9295/supermarkets")
        .then((r) => r.json())
        .then((data) => setSupermarkets(data));
    }, [change]);

    function handleChange(e){
        setEnteredZip(e.target.value)
    }

    function handleSubmit(e) {
        e.preventDefault()
        searchByZip(enteredZip)
        filterResults()
    }

    function searchByZip (zip) {
        fetch(`http://localhost:9295/areas/zipcode/${zip}`)
        .then((r) => r.json())
        .then((data) => {setSearchResults(data)});
    }

    function filterResults(){
        if (searchResults === null){
            setChange(prev=>!prev)
            console.log("zipcode invalid")
        } else if (searchResults === []) {
            console.log("No supermarkets found")
            setSupermarkets(searchResults)
        } else {
            setSupermarkets(searchResults)
        }
    }   

    console.log("search results are:")
    console.log(searchResults)


    return(
        <div>
            <form onSubmit={handleSubmit}>
                <input type="text" name="zipcode" placeholder="your zipcode..." onChange={handleChange}/>
                <button>Submit</button>
            </form>
            {supermarkets.map(s => {
            return (
                <SupermarketCard supermarket={s} setChange={setChange}/>
            )})}
        </div>
    )
}

export default SupermarketList
